import { useState, useEffect } from 'react';
// Api
import { api } from '../api';
// Styles
import { Grid, Typography, FormControl, Button } from '@material-ui/core';

export const SignupPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const [state, setState] = useState<{ [x: string]: string }>({
    email: '',
    password: '',
  });

  // const handleEmail = (e: React.ChangeEvent<HTMLInputElement>) => {
  //   setEmail(e.target.value);
  // };
  // const handlePassword = (e: React.ChangeEvent<HTMLInputElement>) => {
  //   setPassword(e.target.value);
  // };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setState({
      ...state,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    console.log(state);
  };

  return (
    <Grid container component='section' justify='center'>
      <Typography variant='h3'>Регистрация пользователя</Typography>
      <Grid container justify='center'>
        <form onSubmit={handleSubmit}>
          <Grid container justify='center' direction='column'>
            <FormControl
            // className={styles.formControl}
            >
              <label htmlFor='email'>Электронная почта </label>
              <input
                id='email'
                type='text'
                name='email'
                value={state.email}
                // value={email}
                // onChange={handleEmail}
                onChange={handleInputChange}
              />
            </FormControl>
            <FormControl
            // className={styles.formControl}
            >
              <label htmlFor='password'>Пароль </label>
              <input
                id='password'
                type='text'
                name='password'
                // value={password}
                value={state.password}
                // onChange={handlePassword}
                onChange={handleInputChange}
              />
            </FormControl>
            <Button type='submit' variant='outlined'>
              Зарегистрироваться
            </Button>
          </Grid>
        </form>
      </Grid>
    </Grid>
  );
};
