import React, { useRef, useState } from 'react';
// Components
import { IngredientCreationForm } from './components/IngredientCreationForm';

import {refType} from '../types'

import { ingredients } from '../pizzaData';

export const IngredientsListPage = () => {
  const [isEditing, setIsEditing] = useState(false);
  // const btnDelRef = useRef<refType>();

  const handleSetIsEditing = (
    e: React.MouseEvent<HTMLButtonElement, MouseEvent>
  ) => {
    setIsEditing(true);
  };
  const ingredientWillDelit = (
    e: React.MouseEvent<HTMLButtonElement>
  ) => {

    console.log('click dell', e.target.getAttribute('id'));
  };

  return (
    <>
      <section>
        <h3>Доступные ингредиенты</h3>
        <p>
          {ingredients
            .reduce((acc: string[], cur) => {
              acc.push(cur.name);
              return acc;
            }, [])
            .join(', ')}
        </p>
        <div>
          <span>Бекон</span>
          <button onClick={e => handleSetIsEditing(e)}>Редактировать</button>
          <button id="becon" onClick={e => ingredientWillDelit(e)}>
            Удалить
          </button>
        </div>
      </section>
      <section>
        <h3>Это будет новый ингредиент</h3>
        <p>
          <IngredientCreationForm />
        </p>
      </section>
    </>
  );
};
