import React, { useRef, useState } from 'react';
// Components
import { IngredientCreationForm } from './components/IngredientCreationForm';

import { RefBtnType } from '../types';

import { ingredients } from '../pizzaData';

export const IngredientsListPage = () => {
  const [isEditing, setIsEditing] = useState(false);
  const btnDelRef = useRef<HTMLButtonElement>(null);

  const handleSetIsEditing = (
    e: React.MouseEvent<HTMLButtonElement, MouseEvent>
  ) => {
    setIsEditing(true);
  };
  const ingredientWillDelit = (
    // e: React.MouseEvent<HTMLButtonElement, MouseEvent>
  ) => {
    // console.log('click dell', e.currentTarget.getAttribute('id'));
    console.log('click ref dell', btnDelRef.current?.getAttribute("id"));
  };

  return (
    <>
      <section>
        <h3>Доступные ингредиенты</h3>
        <p>
          {ingredients
            .reduce((acc: string[], cur) => {
              acc.push(cur.name);
              return acc;
            }, [])
            .join(', ')}
        </p>
        <div>
          <span>Бекон</span>
          <button type='button' onClick={handleSetIsEditing}>
            Редактировать
          </button>
          <button
            type='button'
            id='becon'
            ref={btnDelRef}
            onClick={e => ingredientWillDelit(e)}
          >
            Удалить
          </button>
        </div>
      </section>
      <section>
        <h3>Это будет новый ингредиент</h3>
        <p>
          <IngredientCreationForm />
        </p>
      </section>
    </>
  );
};
