import React, { useRef, useState } from 'react';
// Components
import { NewIngredientForm } from './components/NewIngredientForm';

import { ingredients } from '../pizzaData';

export const IngredientsListPage = () => {
  const [isEditing, setIsEditing] = useState(false);
  const [isCreating, setIsCreating] = useState(false);
  const btnDelRef = useRef<HTMLButtonElement>(null);
  const btnNewIngrRef = useRef<HTMLButtonElement>(null);

  const handleSetIsEditing = () => {
    setIsEditing(true);
  };
  const ingredientWillDelit = () => {
    const ingredientForDeliting = btnDelRef.current?.getAttribute('id');
    console.log('dell>>', ingredientForDeliting);
  };

  return (
    <>
      <section>
        <h3>Доступные ингредиенты</h3>
        <p>
          {ingredients
            .reduce((acc: string[], cur) => {
              acc.push(cur.name);
              return acc;
            }, [])
            .join(', ')}
        </p>
        <div>
          <span>Бекон</span>
          <button type='button' onClick={handleSetIsEditing}>
            Редактировать
          </button>
          <button
            type='button'
            id='becon'
            ref={btnDelRef}
            onClick={ingredientWillDelit}
          >
            Удалить
          </button>
        </div>
      </section>
      <section>
        <button
          type='button'
          id='newIngredient'
          ref={btnNewIngrRef}
          onClick={ingredientWillCreate}
        >
          Создать новый ингредиент
        </button>
      </section>
      <section>
        <p>
          {isCreating && (
            <NewIngredientForm isEditing={isEditing} isCreating={isCreating} />
          )}
        </p>
      </section>
    </>
  );
};
