import React, { useEffect, useRef, useState } from 'react';
// Components
import { NewIngredientForm } from './components/NewIngredientForm';
// Data
import { ingredients } from '../pizzaData';
import { api } from '../api';

export const IngredientsListPage = () => {
  const [isEditing, setIsEditing] = useState(false);
  const handleSetIsEditing = () => {
    setIsEditing(true);
  };
  const btnDelRef = useRef<HTMLButtonElement>(null);

  const [isCreating, setIsCreating] = useState(false);

  const ingredientWillDelit = (e: React.MouseEvent<HTMLButtonElement>) => {
    // const ingredientForDeliting = btnDelRef.current?.getAttribute('id');
    const ingredientForDeliting = e.currentTarget.getAttribute('id');
    console.log('dell>>', ingredientForDeliting);
  };

  const ingredientWillCreate = () => {
    setIsCreating(true);
  };

  useEffect(() => {
    const getIngredients = async () => {
      const results = await api.ingredients.availableIngredients()
    }
  },[])

  return (
    <>
      <section>
        <h3>Доступные ингредиенты</h3>
        <p>

          {ingredients.map(ingredient => {
            return (
              <>
                <div key={ingredient.slug}>
                  <span>{ingredient.name}</span>
                  <button type='button' onClick={handleSetIsEditing}>
                    Редактировать
                  </button>
                  <button
                    type='button'
                    id={ingredient.slug}
                    onClick={ingredientWillDelit}
                  >
                    Удалить
                  </button>
                </div>
              </>
            );
          })}

        </p>
      </section>

      <section>
        <button type='button' onClick={ingredientWillCreate}>
          Создать новый ингредиент
        </button>
      </section>

      <section>
        <p>{isCreating && <NewIngredientForm isCreating={isCreating} />}</p>
      </section>
    </>
  );
};
