import React from 'react';
import { Route, Switch } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { ProductCreationPage } from './ProductCreationPage';

function App() {
  const { register, handleSubmit } = useForm();

  const onSubmit = handleSubmit((data) => {
    console.log(data)
  });

  return (
    <>
      <Switch>
        <Route exact path='/'>
          <ProductCreationPage></ProductCreationPage>
        </Route>
        <Route path='/login'></Route>
        <Route path='/register'></Route>
      </Switch>
      <form onSubmit={onSubmit}>
        <input ref={register} type='tel' name='price' />
        <input ref={register} type='text' name='name' />
        <input ref={register} type='text' name='asc' />
        <input ref={register} type='file' name='picture' />
        <button>Отправить</button>
      </form>
    </>
  );
}

export default App;
