import React from 'react';
import { useForm } from 'react-hook-form';
import { api } from '../api';
// Type
import { NewIngredient } from '../types';

export const IngredientCreationPage = () => {
  // const { register, handleSubmit } = useForm<NewIngredient>();

  // const onSubmit = handleSubmit((data) => {
  //   const formData = new FormData(data);
  //   console.log(formData);

  //   api.ingredients.createNewIngredient(formData)
  // });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    console.log('target>>>', e.target);

    const formData = new FormData(e.target as HTMLFormElement);
    console.log('FORMDATA>>>', formData);
    const response = await api.ingredients.createNewIngredient(formData.get(''));

    console.log('response>>>', response)
    // try {
    //   const response = await api.ingredients.createNewIngredient(data);
    //   if (response.ok) {
    //     const resutlts = response.json();
    //   } else {
    //     throw new Error('Что-то пошло не так');
    //   }
    // } catch (error) {}
  };
  return (
    <>
      <form id='formIngredients' onSubmit={handleSubmit}>
        {/* <form id="formIngredients" onSubmit={onSubmit}> */}
        <div>
          <label htmlFor='name'>
            Название ингредиента. (Будет показано пользователю)
            <input id='name' type='text' name='name' />
          </label>
        </div>
        <div>
          <label htmlFor='asc'>
            Идентификатор ингредиента
            <input type='text' name='slug' />
          </label>
        </div>
        <div>
          <label htmlFor='price'>
            Цена ингредиента
            <input id='price' type='tel' name='price' />
          </label>
        </div>
        <div>
          <label htmlFor='picture'>
            Категория ингредиента
            <select name='category'>
              <option value='sauces'>Соус</option>
              <option value='cheese'>Сыр</option>
              <option value='vegetables'>Овощ</option>
              <option value='meat'>Мясо</option>
            </select>
          </label>
        </div>
        <div>
          <label htmlFor='picture'>
            Изображение ингредиента
            <input type='file' name='picture' />
          </label>
        </div>
        <button>Отправить</button>
      </form>
    </>
  );
};
