import { yupResolver } from '@hookform/resolvers/yup';
import { useEffect, useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { useMutation } from 'react-query';
import * as yup from 'yup';
import { api } from '../../api';
// Type
import { Ingredient } from '../../types';
// Style
import {
  Typography,
  TextField,
  Input,
  InputLabel,
  FormControl,
  FormHelperText,
  Select,
  MenuItem,
  Button,
  makeStyles,
} from '@material-ui/core';

const schema = yup.object().shape({
  name: yup.string().required('Название - обязательное поле'),
  slug: yup.string().required('Идентификатор - обязательное поле'),
  price: yup
    .number()
    .transform((cv, ov) => (ov === '' ? undefined : cv))
    .typeError('Цена должна быть числом')
    .required('Цена - обязательное поле'),
  // FIXME: do not show error text
  image: yup.mixed().required('Изображение обязательно'),
  thumbnail: yup.mixed().required('Уменьшенное изображение обязательно'),
});

type EditIngredientFormProps = {
  editingIngredient: string;
  ingredient: Ingredient;
  ingredientId: string;
  setIsCancel: (v: boolean) => void;
  setIsEditing: (v: boolean) => void;
};

type EditIngredient = {
  formData: FormData;
  ingredientId: string;
};

const categories = [
  { value: 'size', name: 'Размер' },
  { value: 'dough', name: 'Тесто' },
  { value: 'sauce', name: 'Соус' },
  { value: 'cheese', name: 'Сыр' },
  { value: 'vegetables', name: 'Овощ' },
  { value: 'meat', name: 'Мясо' },
];

const useStyles = makeStyles({
  root: { backgroundColor: 'red', color: 'white' },
  formControl: { marginBottom: 32 },
  input: { marginTop: '0px !important'},
});

function InputStyled() {
  const styles = useStyles();
  return <Input className={styles.input} />;
}

export const EditIngredientForm = ({
  editingIngredient,
  ingredient,
  ingredientId,
  setIsCancel,
  setIsEditing,
}: EditIngredientFormProps) => {
  const styles = useStyles();
  const { register, handleSubmit, errors, control } = useForm<Ingredient>({
    resolver: yupResolver(schema),
  });

  const [currentCategory] = useState(ingredient.category);

  const {
    data: serverResponse,
    isError,
    isLoading,
    mutateAsync: editIngredient,
  } = useMutation(({ formData, ingredientId }: EditIngredient) =>
    api.ingredients.editIngredient(formData, ingredientId)
  );

  const onSubmit = handleSubmit(async data => {
    const { name, slug, price, category, image, thumbnail } = data;
    const formData = new FormData();

    formData.append('name', name);
    formData.append('slug', slug);
    formData.append('price', price);
    formData.append('category', category);
    formData.append('image', image[0]);
    formData.append('thumbnail', thumbnail[0]);

    await editIngredient({ formData, ingredientId });
  });

  useEffect(() => {
    if (!serverResponse?.ok) {
      setIsEditing(true);
      setIsCancel(false);
    } else {
      setIsEditing(false);
      setIsCancel(true);
    }
  });

  if (isError) {
    return <p>Ошибка: что-то пошло не так...</p>;
  }

  if (isLoading) {
    return <p>Загрузка данных...</p>;
  }

  return (
    <>
      <Typography variant='h5'>
        Редактируем ингредиент {editingIngredient}
      </Typography>
      <form onSubmit={onSubmit}>
        <FormControl className={styles.formControl}>
          <label htmlFor='name'>
            Название ингредиента. (Будет показано пользователю)
          </label>
          <Controller
            as={<InputStyled/>}
            // id='name'
            name='name'
            control={control}
            defaultValue={ingredient.name}
            // required
            // fullWidth
          />
          <div>{errors.name?.message}</div>
        </FormControl>

        <FormControl>
          <label htmlFor='slug'>Идентификатор ингредиента</label>
          <Controller
            as={Input}
            id='slug'
            name='slug'
            control={control}
            defaultValue={ingredient.slug}
            required
            fullWidth
          />
          <FormHelperText>{errors.slug?.message}</FormHelperText>
        </FormControl>
        <FormControl>
          <label htmlFor='price'>Цена ингредиента</label>
          <Controller
            as={Input}
            id='price'
            name='price'
            control={control}
            defaultValue={ingredient.price}
            required
            fullWidth
          />
          <div>{errors.price?.message}</div>
        </FormControl>
        <FormControl>
          <label htmlFor='category'>Категория ингредиента</label>
          <Controller
            control={control}
            name='category'
            defaultValue={currentCategory}
            as={
              <Select id='category'>
                {categories.map(c => (
                  <MenuItem key={c.value} value={c.value}>
                    {c.name}
                  </MenuItem>
                ))}
              </Select>
            }
          />
          <div>{errors.category?.message}</div>
        </FormControl>
        <FormControl>
          <label htmlFor='image'>
            Изображение ингредиента
            <input
              id='image'
              name='image'
              ref={register}
              type='file'
              accept='.png'
              hidden
            />
            <Button variant='outlined' component='span'>
              Добавить
            </Button>
          </label>
          <div>{errors.image?.message}</div>
        </FormControl>
        <FormControl>
          <label htmlFor='thumbnail'>
            Уменьшенное изображение ингредиента
            <input
              id='thumbnail'
              ref={register}
              type='file'
              name='thumbnail'
              accept='.png'
              hidden
            />
            <Button variant='outlined' component='span'>
              Добавить
            </Button>
          </label>
          <div>{errors.thumbnail?.message}</div>
        </FormControl>
        {/* {!serverResponse?.ok && <div>Не хватает данных</div>} */}
        <button>Отправить</button>
      </form>
    </>
  );
};
