import styled from 'styled-components'

export const InputField = styled.input`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 12px;
  background-color: #fff;
  border: 2px solid var(--gray200);
  border-radius: 8px;
`;
