import { configureStore } from '@reduxjs/toolkit';
import logger from 'redux-logger';

import { checkout } from '../pages/checkout-page/state/checkoutReducer';
import login from '../pages/login-page/state/loginReducer';
import orders from '../pages/orders-list-page/state/ordersListReducer';
import ingredients from '../pages/pizza-configurator-page/state-ingredients/ingredientsReducer';
import pizza from '../pages/pizza-configurator-page/state-pizza/pizzaReducer';
import signup from '../pages/signup-page/state/signupReducer';

// Store
export const store = configureStore({
  reducer: {
    pizza,
    ingredients,
    orders,
    login,
    signup,
    checkout
  },
  middleware: getDefaultMiddleware => getDefaultMiddleware().concat(logger),
  devTools: process.env.NODE_ENV !== 'production',
});
