import styled from 'styled-components'
export const Legend = styled.legend`
  font-weight: 500;
  font-size: 14px;
  line-height: 20px;
  margin-bottom: 6px;
  @media (min-width: 960px) {
    font-size: 16px;
    line-height: 24px;
    margin-bottom: 8px;
  }
`;
