import styled from 'styled-components';

export const InputField = styled.input`
  display: flex;
  align-items: center;
  padding: 12px;
  background-color: #fff;
  border: 2px solid var(--gray200);
  border-radius: 8px;
  width: 100%;
  margin-bottom: 16px;
  @media (min-width: 960px) {
    padding: 16px;
  }
`;
export const InputFieldwPlaceholder = styled(InputField)`
  ::placeholder {
    font-size: 16px;
    line-height: 24px;
    @media (min-width: 960px) {
      font-size: 20px;
    }
  }
`;
