import { Fragment } from 'react';
import styled from 'styled-components';

const SwitcherContainer = styled.fieldset`
  display: inline-flex;
  flex-direction: row;
  align-items: center;
  padding: 2px;
  background: var(--gray100);
  border-radius: 12px;
  overflow: auto;
`;
// const SwitcherItem = styled.div`
const Label = styled.label`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 10px;
  padding: 4px 12px;
  background: #ffffff;
  border-radius: 12px;
  color: var(--gray100);
`;

const Input = styled.input`
`

const SwitcherItem = styled.div`

  &:checked + label {
    color: var(--black);
  }
  color: ${({ checked }) => (checked ? 'var(--black)' : 'var(--gray600)')};
  background-color: ${({ isSelected }) =>
    isSelected ? '#fff' : 'transparent'};
  font-weight: ${({ isSelected }) => (isSelected ? '500' : '400')};
  box-shadow: ${({ isSelected }) => isSelected && 'var(--shadow)'};
  cursor: ${({ isSelected }) => (isSelected ? 'default' : 'pointer')};
`;

