import { Fragment } from 'react';
import styled from 'styled-components';
// Styles
import { Legend } from '../../../share/styled-components/Legend';
import { RadioGroupContainer } from '../../../share/styled-components/RadioGroupContainer';
import { Primary } from '../../../share/styled-components/Button';

const RadioBtnContainer = styled(RadioGroupContainer)`
  display: flex;
  flex-wrap: wrap;

  @media (min-width: 960.5px) {
    background-color: #fff;
    border: none;
  }
`;
const Label = styled.label`
  ${ButtonSecondary}
`;

// const Label = styled.label`
//   display: flex;
//   flex-direction: row;
//   justify-content: center;
//   align-items: center;
//   border-radius: 10px;
//   padding: 4px 12px;

//   border-radius: 12px;
//   background-color: transparent;
//   font-size: 14px;
//   line-height: 20px;
//   cursor: pointer;
//   @media (min-width: 960.5px) {
//     padding: 4px 12px;
//     font-size: 16px;
//     line-height: 24px;
//   }
// `;
const Input = styled.input`
  &:checked + ${ButtonSecondary} {
    color: var(--black);
    background-color: #fff;
    font-weight: 500;
    box-shadow: var(--shadow);
    cursor: default;
  }
`;

export const RadioGroupBtn = props => {
  const { register, legend, name, options } = props;

  return (
    <>
      <fieldset>
        <Legend>{legend}</Legend>
        <RadioBtnContainer>
          {Object.entries(options).map(option => (
            <Fragment key={option[1].id}>
              <Input
                ref={register}
                type='radio'
                name={name}
                value={option[1].slug}
                hidden
              />
              <Label>
                {option[1].name}
                {name === 'size' ? ' см' : null}
              </Label>
            </Fragment>
          ))}
        </RadioBtnContainer>
      </fieldset>
    </>
  );
};
