import React, { Fragment } from 'react';
import { useSelector } from 'react-redux';
import { useForm, Controller } from 'react-hook-form';
import { calculateTotalPrice } from '../../../share/calculateTotalPrice';
import styled from 'styled-components';
// Selectors
import {
  getIngredients,
  getIngredientsByCategory,
} from '../state-ingredients/ingredientsSelectors';

// Data
import { DEFAULT_PIZZA } from '../../../pizzaData';
// Components
import { CheckboxGroup } from './CheckboxGroup';
import { RadioGroup } from './RadioGroup';
import { OrderPreview } from '../../../share/components/OrderPreview';
import { RadioGroupWithRef } from './RadioGroupWithRef';
import { RadioGroupSwitcher } from './RadioGroupSwitcher';

// Styles
const FieldsetRadioGroupSwitcher = styled.div`
  margin: 0;
  padding: 0;
  border: none;
  display: inline-block;
  flex-direction: column;
  margin-bottom: 8px;
  /* align-items: center; */
  padding: 2px;
  & legend {
    margin: 0;
    padding: 0;
    border: none;
    font-weight: 500;
    font-size: 14px;
    line-height: 20px;
    @media (min-width: var(--point-desktop)) {
      font-size: 16px;
      line-height: 24px;
    }
  }

  & div {
    display: flex;
    flex-direction: row;
    align-items: center;
    padding: 2px;
    background: var(--gray100);
    border-radius: 12px;

    & input[type='radio'] {
      display: none;
    }

    & label {
      display: flex;
      flex-direction: row;
      justify-content: center;
      align-items: center;
      padding: 4px 12px;
      cursor: pointer;
      user-select: none;
    }
    & input[type='radio']:checked & label {
      background: #ffffff;
      box-shadow: var(--shadow);
      border-radius: 10px;
    }
  }

  & + & {
    margin-right: 8px;
  }
`;

export const PizzaForm = ({ onPizzaOrder }) => {
  const { register, handleSubmit, watch, control } = useForm({
    defaultValues: DEFAULT_PIZZA,
  });

  const ingredients = useSelector(getIngredients);

  const SIZE = useSelector(getIngredientsByCategory('size'));
  const DOUGH = useSelector(getIngredientsByCategory('dough'));
  const SAUCES = useSelector(getIngredientsByCategory('sauces'));
  const CHEESE = useSelector(getIngredientsByCategory('cheese'));
  const VEGETABLES = useSelector(getIngredientsByCategory('vegetables'));
  const MEAT = useSelector(getIngredientsByCategory('meat'));

  const values = watch();

  console.log('values', values);

  const totalPrice = calculateTotalPrice(ingredients, values);

  const onSubmit = handleSubmit(data => {
    if (onPizzaOrder) {
      onPizzaOrder(data);
    }
  });

  return (
    <>
      <OrderPreview pizza={values} ingredients={ingredients} />
      <form onSubmit={onSubmit}>
        {/* <div>
          <RadioGroupSwitcher register={register} values={SIZE} />
        </div> */}
        <Controller
          render={(props)=>{return Object.entries(SIZE).map(option => (
            <Fragment>
              <input
                type='radio'
                value={option[1].slug}
                onChange={e => props.onChange(e.target.checked)}
                checked={props.value}
              />
              <label key={option[1].id}>{SIZE[1].name}см</label>
            </Fragment>
          ));}}
          // as={
          //   <FieldsetRadioGroupSwitcher>
          //     <RadioGroupWithRef
          //       legend='Размер'
          //       ref={register}
          //       options={SIZE}
          //       name='size'
          //     />
          //   </FieldsetRadioGroupSwitcher>
          // }
          control={control}
          name='size'
        />
        {/* <FieldsetRadioGroupSwitcher>
          <RadioGroup
            legend='Размер'
            register={register}
            name='size'
            options={SIZE}
          />
        </FieldsetRadioGroupSwitcher> */}
        <RadioGroup
          legend='Тесто'
          register={register}
          name='dough'
          options={DOUGH}
        />
        <RadioGroup
          legend='Выберите соус'
          register={register}
          name='sauces'
          options={SAUCES}
        />

        <CheckboxGroup
          legend='Добавьте сыр'
          register={register}
          name='cheese'
          options={CHEESE}
        />

        <CheckboxGroup
          legend='Добавьте овощи'
          register={register}
          name='vegetables'
          options={VEGETABLES}
        />

        <CheckboxGroup
          legend='Добавьте мясо'
          register={register}
          name='meat'
          options={MEAT}
        />

        <button>Заказать за {totalPrice}руб.</button>
      </form>
    </>
  );
};
