export const DEFAULT_PIZZA = Object.freeze({
  size: '30',
  dough: 'thin',
  sauces: 'tomato-sauce',
  cheese: [],
  vegetables: [],
  meat: [],
});

export const PIZZA_DELIVERY_PRICE = 180;

export const PIZZA_DELIVERY_STATUS = Object.freeze({
  0: 'доставляется',
  1: 'повторить заказ',
});

export const PIZZA_DELIVERY = Object.freeze({
  price: 180,
  status: [
    { 0: 'доставляется' },
    { 1: 'повторить заказ' }],
});

export const fakeOrder = {
  pizza: {
    size: '30',
    dough: 'thin',
    sauces: 'tomato-sauce',
    cheese: [],
    vegetables: ['tomato'],
    meat: ['ham'],
  },
  address: 'Ivanovskaya street 7-1',
  cardName: 'Ivan Ivanov',
  cardNumber: '4545454545454545',
};

export const IngredientsCases = [
  { slug: 'thin', case: 'тонком' },
  { slug: 'thick', case: 'толстом' },
];

