    };

  if (isLoading) {
    return (
      <div>
        <Loader />
      </div>
    );
  }

  return (
    <>
      {orders.length ? (
        orders.map((order, i) => (
          <Order
            key={order.id}
            order={order}
            pizza={order.pizza}
            sequence={i}
            isInterval={isInterval}
            repeatPizzaOrder={repeatPizzaOrder}
          />
        ))
      ) : (
        <span>Вы пока не сделали ни одного заказа</span>
      )}
    </>
  );
};
