import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
// ActionTypes
import { loginActionTypes } from './loginActionTypes';

const initialState = {
  login: { password: '', email: '' },
  isRegistered: null,
  isLoading: false,
  error: null,
};

export const login = createSlice({
  name: 'login',
  initialState,
  reducers: {
    startFetching: state => {
      state.isLoading = true;
    },
    stopFetching: state => {
      state.isLoading = true;
    },
    setFetchingError: (state, action) => {
      state.isLoading = false;
      state.error = action.payload;
    },
    loginFill: (state, action) => {
      state.login = action.payload;
      state.isLoading = false;
      state.error = null;
    },
    loginSetStatus: (state, action) => {
      state.isRegistered = action.payload;
      state.isLoading = false;
      state.error = null;
    },
    extraReducers:{}
  },
});


export const checkUserAsync = createAsyncThunk(
  loginActionTypes.LOGIN_FETCH_ASYNC,
  async (credentials, thunkAPI) => {
    const response = await api.users.login(credentials);
    if (response.status === 200) {
      const results = await response.json();

      thunkAPI.dispatch(setUserStatus(results));
    } else {
      const error = {
        status: response.status,
      };
      thunkAPI.dispatch(setFetchingError(error));
    }
    thunkAPI.dispatch(stopFetching());
  }
);
