import { useEffect, useState } from "react"

export const ImageIcon = ({ img, width }) => {

  const [image, setImage] = useState('');

  useEffect(() => {
    if (img) {
      setImage(img)
    }
  },[img])

  console.log('img', !!img);
  return (
    <img
      src={`${process.env.PUBLIC_URL}/img/${img}.svg`}
      width={width}
      alt={img}
    />
  );
};
