import { useState } from 'react'
import { yupResolver } from '@hookform/resolvers/yup';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import * as yup from 'yup';
import styled from 'styled-components';
// Hooks
import { useWindowDimensions } from '../../../share/hooks/useWindowDimentions';
// Data
import { PIZZA_DELIVERY } from '../../../pizzaData';
import { PAYMENT_CARD } from '../../../pizzaData';
// Helpers
import { calculateTotalPrice } from '../../../share/calculateTotalPrice';
// Actions
import { checkoutReducer, sendOrderAsync } from '../state/checkoutReducer';
import { getOrder } from '../state/checkoutSelectors';
// Components
import { Order } from '../../../share/components';
import { Image } from './Image';
// Styles
import { ButtonPrimary } from '../../../share/styled-components/Button';
import {
  InputFieldwPlaceholder,
  InputField,
} from '../../../share/styled-components/InputField';
const Section = styled.section`
  padding-bottom: 160px;
  @media (min-width: 960px) {
    padding-bottom: 0px;
  }
`;

const OrderPreview = styled(Order)`
  grid-area: order;
  @media (min-width: 960px) {
    max-width: 350px;
  }
`;
const FormContent = styled.div`
  grid-area: form;
  @media (min-width: 960px) {
    max-width: 530px;
  }
`;
const Form = styled.form`
  display: grid;
  grid-template-columns: 1fr;
  grid-template-areas:
    'order'
    'form'
    'payment';
  @media (min-width: 960px) {
    grid-template-columns: 1fr 350px;
    grid-template-rows: 0.6fr 1.4fr;
    gap: 0px 80px;
    grid-template-areas:
      'form order'
      'form payment';
  }
`;

const Legend = styled.legend`
  font-weight: 500;
  font-size: 16px;
  line-height: 24px;
  color: var(--black);
  margin-bottom: 16px;
  @media (min-width: 960px) {
    font-size: 20px;
    margin-bottom: 24px;
  }
`;

const FormSection = styled.div`
  padding-bottom: 16px;
  border-bottom: 1px solid var(--gray200);
  & + & {
    margin-top: 16px;
    @media (min-width: 960px) {
      margin-top: 24px;
    }
  }
  @media (min-width: 960px) {
    padding-bottom: 24px;
  }
`;
const AddressDetails = styled.div`
  display: flex;
  margin-bottom: -12px;
  @media (min-width: 960px) {
    margin-bottom: -16px;
  }
  label {
    color: var(--gray600);
    input {
      margin-top: 4px;
    }
  }
`;
const CardDetails = styled.div`
  display: flex;
  justify-content: space-between;
`;
const OrderPayment = styled.footer`
  grid-area: payment;
  width: 100%;
  height: fit-content;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 12px 16px;
  background-color: #fff;
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  box-shadow: 0px -16px 32px rgba(75, 75, 124, 0.05),
    0px 0px 4px rgba(75, 75, 124, 0.1);
