import { yupResolver } from '@hookform/resolvers/yup';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import { useSelector } from 'react-redux';
import * as yup from 'yup';
import styled from 'styled-components';
// Hooks
import { useWindowDimensions } from '../../../share/hooks/useWindowDimentions';
// Data
import { PIZZA_DELIVERY } from '../../../pizzaData';
// Helpers
import { calculateTotalPrice } from '../../../share/calculateTotalPrice';
// Actions
import { checkoutReducer, sendOrderAsync } from '../state/checkoutReducer';
import { getOrder } from '../state/checkoutSelectors';
// Styles
import { Order } from '../../../share/components';
import { ButtonPrimary } from '../../../share/styled-components/Button';
import {
  InputFieldwPlaceholder,
  InputField,
} from '../../../share/styled-components/InputField';
const Section = styled.section`
  padding-bottom: 160px;
  @media (min-width: 960px) {
    padding-bottom: 0px;
  }
`;

const OrderPreview = styled(Order)`
  grid-area: order;
  @media (min-width: 960px) {
    max-width: 350px;
  }
`;
const FormContent = styled.div`
  grid-area: form;
  @media (min-width: 960px) {
    max-width: 530px;
  }
`;
const Form = styled.form`
  display: grid;
  grid-template-columns: 1fr;
  grid-template-areas:
    'order'
    'form'
    'payment';
  @media (min-width: 960px) {
    grid-template-columns: 1fr 350px;
    grid-template-rows: 0.6fr 1.4fr;
    gap: 0px 80px;
    grid-template-areas:
      'form order'
      'form payment';
  }
`;

const Legend = styled.legend`
  font-weight: 500;
  font-size: 16px;
  line-height: 24px;
  color: var(--black);
  margin-bottom: 16px;
  @media (min-width: 960px) {
    font-size: 20px;
    margin-bottom: 24px;
  }
`;
const FormSection = styled.div`
  padding-bottom: 16px;
  border-bottom: 1px solid var(--gray200);
  & + & {
    margin-top: 16px;
    @media (min-width: 960px) {
      margin-top: 24px;
    }
  }
  @media (min-width: 960px) {
    padding-bottom: 24px;
  }
`;
const AddressDetails = styled.div`
  display: flex;
  margin-bottom: -12px;
  @media (min-width: 960px) {
    margin-bottom: -16px;
  }
  label {
    color: var(--gray600);
    input {
      margin-top: 4px;
    }
  }
`;
const CardDetails = styled.div`
  display: flex;
  justify-content: space-between;
`;
const OrderPayment = styled.footer`
  grid-area: payment;
  width: 100%;
  height: fit-content;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 12px 16px;
  background-color: #fff;
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  box-shadow: 0px -16px 32px rgba(75, 75, 124, 0.05),
    0px 0px 4px rgba(75, 75, 124, 0.1);
  @media (min-width: 960px) {
    position: relative;
    max-width: 350px;
    box-shadow: none;
    background-color: transparent;
  }
`;
const OrderPaymentSection = styled.p`
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 4px;
  font-weight: ${({ toPay }) => toPay && '500'};
  margin-top: ${({ toPay }) => toPay && '18px'};
  @media (min-width: 480px) {
    font-size: 14px;
    line-height: 20px;
    margin-bottom: 8px;
  }
`;
const DividingLine = styled.div`
  width: 100%;
  border-bottom: 1px dashed var(--gray200);
  padding-bottom: 4px;
  @media (min-width: 480px) {
    padding-bottom: 8px;
  }
`;
const CheckoutFormButton = styled(ButtonPrimary)`
  margin-top: 16px;
  @media (min-width: 960px) {
    width: 100%;
    margin-top: 32px;
  }
`;
const ArtemPromises = styled.p`
  margin-top: 24px;
`;

const valid = require('card-validator');

const normalizeCardNumber = value => {
  return (
    value
      .replace(/\s/g, '')
      .match(/.{1,4}/g)
      ?.join(' ')
      .substr(0, 19) || ''
  );
};

const schema = yup.object().shape({
  address: yup.string().required('Это обязательное поле'),
  cardNumber: yup.string().required('Это обязательное поле'),
  cardName: yup.string().required('Это обязательное поле'),
});

/**
 * Компонент собирает данные пользователя для оплаты заказа и отправляет на сервер
 * The component collects user data for order payment and sends it to the server
 * @param pizza
 * @param ingredients
 */
export const CheckoutForm = ({ pizza, ingredients }) => {
  const dispatch = useDispatch();
  const order = useSelector(getOrder);
  const { width: windowWidth } = useWindowDimensions();

  const { register, handleSubmit, errors, setValue, watch } = useForm({
    resolver: yupResolver(schema),
  });

  const сardNumber = watch('cardNumber');
  let numberValidation = valid.number(сardNumber);

  const onSubmit = handleSubmit(data => {
    let order = null;
    order = {
      pizza: pizza,
      address: data.address,
      cardName: data.cardName,
      cardNumber: data.cardNumber,
    };
    if (order) {
      dispatch(checkoutReducer.actions.fillOrder(order));
      dispatch(sendOrderAsync(order));
    }
  });

  const orderPrice = calculateTotalPrice(ingredients, pizza);

  return (
    <Section>
      <Form onSubmit={onSubmit}>
        <OrderPreview order={order} pizza={pizza} />
        <FormContent className='form'>
          <FormSection>
            <Legend>Адрес доставки</Legend>
            <label htmlFor='address'>
              <InputFieldwPlaceholder
                ref={register}
                name='address'
                id='address'
                type='text'
                placeholder='Введите адрес'
              />
              <div>{errors.address?.message}</div>
            </label>
            <AddressDetails>
              <label htmlFor='porch'>
                подъезд
                <InputField
                  width='104'
                  margin='8'
                  ref={register}
                  name='porch'
                  id='porch'
                  type='tel'
                />
              </label>
              <label htmlFor='flow'>
                этаж
                <InputField
                  width='104'
                  margin='8'
                  ref={register}
                  name='flow'
                  id='flow'
                  type='tel'
                />
              </label>
              <label htmlFor='flat'>
                квартира
                <InputField
                  width='104'
                  ref={register}
                  name='flat'
                  id='flat'
                  type='tel'
                />
              </label>
            </AddressDetails>
          </FormSection>
          <FormSection>
            <Legend>Данные для оплаты</Legend>
            <label htmlFor='cardNumber'>
              <InputFieldwPlaceholder
                ref={register}
                name='cardNumber'
                id='cardNumber'
                type='tel'
                inputMode='numeric'
                autoComplete='cc-number'
                placeholder='Номер карты'
                onChange={e => {
                  const { value } = e.target;
                  setValue('cardNumber', normalizeCardNumber(value));
                }}
              />
              <div>{errors.cardNumber?.message}</div>
              <span>{numberValidation.card && numberValidation.card.type}</span>
            </label>
            <CardDetails>
              <label htmlFor='year'>
                <InputFieldwPlaceholder
                  width={windowWidth >= 960 ? '128' : '112'}
                  ref={register}
                  name='year'
                  id='year'
                  type='tel'
                  placeholder='MM/YYYY'
                />
              </label>
              <label htmlFor='CVV'>
                <InputFieldwPlaceholder
                  width={windowWidth >= 960 ? '70' : '60'}
                  ref={register}
                  name='CVV'
                  id='CVV'
                  type='tel'
                  placeholder='CVV'
                />
              </label>
            </CardDetails>
            <label htmlFor='cardName'>
              <InputFieldwPlaceholder
                ref={register}
                name='cardName'
                id='cardName'
                type='text'
                placeholder='Имя как на карте'
              />
              <div>{errors.cardName?.message}</div>
            </label>
          </FormSection>
          <ArtemPromises>
            Доставим пиццу в течение часа или вернем деньги. Артем слов на ветер
            не бросает.
          </ArtemPromises>
        </FormContent>
        <OrderPayment className='payment'>
          <OrderPaymentSection>
            <span>Стоимость заказа</span> <span>{orderPrice} руб.</span>
          </OrderPaymentSection>
          <OrderPaymentSection>
            Доставка <span>{PIZZA_DELIVERY.price} руб.</span>
          </OrderPaymentSection>
          <DividingLine />
          <OrderPaymentSection toPay>
            К оплате <span>{orderPrice + PIZZA_DELIVERY.price} руб.</span>
          </OrderPaymentSection>
          <CheckoutFormButton type='submit' onClick={onSubmit}>
            Оплатить {orderPrice + PIZZA_DELIVERY.price} руб.
          </CheckoutFormButton>
        </OrderPayment>
      </Form>
    </Section>
  );
};
