import styled from 'styled-components'

const InputField = styled.input`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 12px;
  background-color: #fff;
  border: 2px solid #e1e1ed;
  border-radius: 8px;
`;
