import { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import styled from 'styled-components';
// Actions
import { fetchIngredientsAsync } from '../../pages/pizza-configurator-page/state-ingredients/ingredientsReducer';
// Selectors
import { getIngredients } from '../../pages/pizza-configurator-page/state-ingredients/ingredientsSelectors';
// Components
import { OrderPreview } from './OrderPreview';
// Icons
import { ReactComponent as IconDelivery } from '../../asserts/icons/icn_delivery.svg';
// Styles
const Article = styled.article`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 16px;
  background: #fff;
  border-radius: 16px;
  box-shadow: 0px 8px 16px rgba(75, 75, 124, 0.05);
  margin-bottom: 16px;
  max-width: 350px;
  @media (min-width: 960px) {
    width: 350px;
  }
`;

const OrderPreviewHeader = styled.header`
  margin-bottom: 16px;
  span {
    margin-right: 16px;
    @media (min-width: 960px) {
      margin-right: 38px;
    }
  }
`;

const Title = styled.h3`
  font-weight: 500;
  font-size: 20px;
  line-height: 24px;
  color: var(--black);
  margin: 0;
  margin-bottom: 8px;
`;

const OrderPreviewFooter = styled.footer`
  display: flex;
  align-items: center;
`;

export const Order = ({ order }) => {
  const { cardNumber, pizza } = { ...order };
  const dispatch = useDispatch();

  console.log('Order pizza', pizza);

  useEffect(() => {
    dispatch(fetchIngredientsAsync());
  }, [dispatch]);

  const ingredients = useSelector(getIngredients);

  return (
    <Article>
      <OrderPreviewHeader>
        Заказ <span>2390</span>
        <time>21 октября 2020, 13:40</time>
      </OrderPreviewHeader>
      <div>
        <Title>Ленивая Маргарита</Title>
        <OrderPreview pizza={pizza} ingredients={ingredients} />
      </div>
      <hr />
      <OrderPreviewFooter>
        <span>420</span> руб
        <span> • </span>
        оплата MC <span>{`*${cardNumber.slice(-4)} `}</span>
        <span>
          <IconDelivery fill='orange' />
          Доставляется
        </span>
      </OrderPreviewFooter>
    </Article>
  );
};
