import React from 'react';
// Helpers
import { calculateTotalPrice } from '../../../calculateTotalPrice';
// Data
import { CHEESE, DOUGH, MEAT, SAUCE, SIZE, VEGETABLES } from '../../pizzaData';
// Types
import { PizzaConfiguration } from '../../../types';

interface CheckoutPreviewProps {
  pizza: PizzaConfiguration;
}

export const CheckoutPreview: React.FC<CheckoutPreviewProps> = ({
  pizza,

}) => {
  const { size, dough, sauce, cheese, vegetables, meat } = pizza;



  return (
    <>
      <section>
        <h3>Ленивая Маргарита</h3>
        <p>
          {size} см на {dough} тесте• {sauce} соус • {cheese} • {vegetables} •{' '}
          {meat}
        </p>
        <p>30 см на толстом тесте• {sauce} соус • Моцарелла • Томаты • Бекон</p>
        <hr />
        <p>{calculateTotalPrice(pizza)} руб.</p>
      </section>
    </>
  );
};
