import styled from 'styled-components';
// Styles
import { Legend } from '../../../share/styled-components/Legend';
const Fieldset = styled.fieldset`
  display: flex;
  flex-wrap: nowrap;
  color: var(--black);
  font-weight: 500;
`;
const CheckboxContainer = styled.div`
  position: relative;
  width: 104px;
  height: 128px;
  @media (min-width: 960.5px) {
    width: 112px;
    height: 156px;
  }
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  padding: 0 12px 12px;
  background: #ffffff;
  box-shadow: 0px 8px 16px rgba(75, 75, 124, 0.05);
  border: 2px solid transparent;
  transition: border 0.2s ease;
  border-radius: 12px;
  & + & {
    margin-left: 8px;
    @media (min-width: 960.5px) {
      margin-left: 16px;
    }
  }
  &:hover,
  &:active {
    border: 2px solid #00a896;
    transition: border 0.2s ease;
  }
`;

const Image = styled.div`
  position: relative;
  display: block;
  width: 64px;
  height: 64px;
  background: paleturquoise;
  margin-bottom: 8px;
  @media (min-width: 960.5px) {
    margin-bottom: 12px;
  }
  top: -3.8px;
  @media (min-width: 960.5px) {
    top: -7.75px;
  }
`;

const Label = styled.label`
  display: block;
  margin-bottom: 8px;
  @media (min-width: 960.5px) {
    margin-bottom: 12px;
  }
`;
const PriceInputContainer = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;
const Price = styled.span`
  font-weight: 500;
  font-size: 16px;
`;

export const CheckboxGroup = ({ register, legend, options, name }) => {
  return (
    <Fieldset>
      <Legend>{legend}</Legend>
      {Object.entries(options).map(option => {
        console.log('option>>>', option);
        return (
          <CheckboxContainer>
            <Image />
            <Label htmlFor={option[1].id} key={option[1].id}>
              {option[1].name}
            </Label>
            <PriceInputContainer>
              <Price>{option[1].price}â‚½</Price>
              <input
                id={option[1].id}
                ref={register}
                type='checkbox'
                name={name}
                value={option[1].slug}
              />
            </PriceInputContainer>
          </CheckboxContainer>
        );
      })}
    </Fieldset>
  );
};
