import { useEffect, useState } from "react"

export const ImageIcon = ({ cardImageName, width }) => {
  const [image, setImage] = useState('');

  useEffect(() => {
    console.log('img>>>', cardImageName);
    if (cardImageName) {
      setImage(cardImageName);
    }
    console.log('image>>>', image);
  }, [cardImageName]);

  // console.log('img', !!img);
  return (
    <img
      src={`${process.env.PUBLIC_URL}/img/${image}.svg`}
      width={width}
      alt={cardImageName}
    />
  );
};
