import { Fragment } from 'react';
import styled from 'styled-components';

const SwitcherContainer = styled.fieldset`
  display: inline-flex;
  flex-direction: row;
  align-items: center;
  padding: 2px;
  background: var(--gray100);
  border-radius: 12px;
  overflow: auto;
`;
// const SwitcherItem = styled.div`
const Label = styled.label`
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  border-radius: 10px;
  padding: 4px 12px;
  background: #ffffff;
  border-radius: 12px;
  background-color: transparent;
  cursor: pointer;
`;

const Input = styled.input`
  &:checked + ${Label} {
    color: var(--black);
    background-color: #fff;
    font-weight: 500;
    box-shadow: var(--shadow);
  }
`;

export const RadioGroupSwitcher = props => {
  const { legend, options, register, name } = props;

  console.log(options);

  return (
    <>
      <div>
        <legend>{legend}</legend>
        <SwitcherContainer>
          {Object.entries(options).map(option => (
            <Fragment key={option[1].id}>
              <Input
                id={option[1].id}
                ref={register}
                type='radio'
                name={name}
                value={option[1].slug}
                hidden
              />
              <Label htmlFor={option[1].id}>
                {option[1].name}
                {name === 'size' ? 'см' : null}
              </Label>
            </Fragment>
          ))}
        </SwitcherContainer>
        {/* </fieldset> */}

        {/* {values.map((value, i) => (
          <SwitcherItem isSelected={i === 0}>{value.name}</SwitcherItem>
        ))} */}
      </div>
    </>
  );
};
