import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { ReactComponent as NavbarBrand } from '../../asserts/NavbarBrand.svg';

export const StyledHeader = styled.header`
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: #ffffff;

`;

const StyledWrapper = styled.div`
  box-shadow: 0px 0px 2px 0px #2e3137 15%, 0px 3px 4px 0px #2e3137 5%;

  @media (max-width: 360px) {
    min-height: 56px;
    padding: 8px;
  }
  @media (max-width: 960px) {
    min-height: 80px;
    padding: 16px;
  }
`;


export const Header = () => {
  return (
    <StyledHeader>
      <Link to='/'>
        <NavbarBrand height={24} />
      </Link>
      <Link to='/login'>Личный кабинет</Link>
    </StyledHeader>
  );
};
