import React from 'react';
// Helpers
import { calculateTotalPrice} from '../../'
import { PizzaConfiguration } from '../../../types';

interface CheckoutPreviewProps {
  pizza: PizzaConfiguration;
  price: number
}

export const CheckoutPreview: React.FC<CheckoutPreviewProps> = ({ pizza, price }) => {
  const { size, dough, sauce, cheese, vegetables, meat } = pizza;

  return (
    <>
      <section>
        <h3>Ленивая Маргарита</h3>
        <p>
          {size} см на {dough} тесте• {sauce} соус • {cheese} • {vegetables} • {meat}
        </p>
        <p>30 см на толстом тесте• {sauce} соус • Моцарелла • Томаты • Бекон</p>
        <hr />
        <p>{price} руб.</p>
      </section>
    </>
  );
};
