import { createReducer, createSlice } from '@reduxjs/toolkit';

import { pizzaActionsTypes } from './pizzaActionsTypes';

const initialState = {
  pizza: {
    size: '30',
    dough: 'thin',
    sauce: 'tomato-sauce',
    cheese: [],
    vegetables: [],
    meat: [],
  },
};

export const pizzaReducer = createSlice({
  name: 'pizza',
  initialState,
  reducers: {
    fillPizza: (state, action) => {
      state.pizza = action.payload;
    },
    resetPizza: state => {
      state = {...initialState};
    },
  },
});
