import { yupResolver } from '@hookform/resolvers/yup';
import { useForm } from 'react-hook-form';
import { useDispatch } from 'react-redux';
import * as yup from 'yup';
import styled from 'styled-components';
// Data
import { PIZZA_DELIVERY } from '../../../pizzaData';
// Helpers
import { calculateTotalPrice } from '../../../share/calculateTotalPrice';
// Actions
import { checkoutReducer, sendOrderAsync } from '../state/checkoutReducer';
// Styles
import { ButtonPrimary } from '../../../share/styled-components/Button';
import {
  InputFieldwPlaceholder,
  InputField,
} from '../../../share/styled-components/InputField';
const Legend = styled.legend`
  font-weight: 500;
  font-size: 16px;
  line-height: 24px;
  color: var(--black);
  margin-bottom: 16px;
  @media (min-width: 960px) {
    font-size: 20px;
    margin-bottom: 24px;
  }
`;
const FormSection = styled.div`
  padding-bottom: 16px;
  border-bottom: 1px solid var(--gray200);
  & + & {
    margin-top: 16px;
    @media (min-width: 960px) {
      margin-top: 24px;
    }
  }
  @media (min-width: 960px) {
    padding-bottom: 24px;
  }
`;
const AddressDetails = styled.div`
  display: flex;
  margin-bottom: -12px;
  @media (min-width: 960px) {
    margin-bottom: -16px;
  }
  label {
    color: var(--gray600);
    input {
      margin-top: 4px;
    }
  }
`;
const CardDetails = styled.div`
  display: flex;
  justify-content: space-between;
`;
const OrderFooter = styled.footer`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  padding: 12px 16px;
  background-color: #fff;
  background-color: paleturquoise;
  position: fixed;
  bottom: 0;
  left: 0;
  right: 0;
  box-shadow: var(--shadow);
  padding: 12px 16px;
  @media (min-width: 960px) {
    position: relative;
  }
`;
const OrderFooterSection = styled.p`
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 4px;
  font-weight: ${({ toPay }) => toPay && '500'};
  margin-top: 18px;
  @media (min-width: 480px) {
    font-size: 14px;
    line-height: 20px;
    margin-bottom: 8px;
  }
`;
const DividingLine = styled.div`
  width: 100%;
  border-bottom: 1px dashed var(--gray200);
  padding-bottom: 4px;
  @media (min-width: 480px) {
    padding-bottom: 8px;
  }
`;
const CheckoutFormButton = styled(ButtonPrimary)`
  margin-top: 16px;
  @media (min-width: 960px) {
    margin-top: 32px;
  }
`;

const valid = require('card-validator');

const normalizeCardNumber = value => {
  return (
    value
      .replace(/\s/g, '')
      .match(/.{1,4}/g)
      ?.join(' ')
      .substr(0, 19) || ''
  );
};

const schema = yup.object().shape({
  address: yup.string().required('Это обязательное поле'),
  cardNumber: yup.string().required('Это обязательное поле'),
  cardName: yup.string().required('Это обязательное поле'),
});

/**
 * Компонент собирает данные пользователя для оплаты заказа и отправляет на сервер
 * The component collects user data for order payment and sends it to the server
 * @param pizza
 * @param ingredients
 */
export const CheckoutForm = ({ pizza, ingredients }) => {
  const dispatch = useDispatch();

  const { register, handleSubmit, errors, setValue, watch } = useForm({
    resolver: yupResolver(schema),
  });

  const сardNumber = watch('cardNumber');
  let numberValidation = valid.number(сardNumber);

  const onSubmit = handleSubmit(data => {
    let order = null;
    order = {
      pizza: pizza,
      address: data.address,
      cardName: data.cardName,
      cardNumber: data.cardNumber,
    };
    if (order) {
      dispatch(checkoutReducer.actions.fillOrder(order));
      dispatch(sendOrderAsync(order));
    }
  });

  const orderPrice = calculateTotalPrice(ingredients, pizza);

  return (
    <>
      <section>
        <form onSubmit={onSubmit}>
          <FormSection>
            <Legend>Адрес доставки</Legend>
            <label htmlFor='address'>
              <InputFieldwPlaceholder
                ref={register}
                name='address'
                id='address'
                type='text'
                placeholder='Введите адрес'
              />
              <div>{errors.address?.message}</div>
            </label>
            <AddressDetails>
              <label htmlFor='porch'>
                подъезд
                <InputField
                  width='104'
                  margin='8'
                  ref={register}
                  name='porch'
                  id='porch'
                  type='tel'
                />
              </label>
              <label htmlFor='flow'>
                этаж
                <InputField
                  width='104'
                  margin='8'
                  ref={register}
                  name='flow'
                  id='flow'
                  type='tel'
                />
              </label>
              <label htmlFor='flat'>
                квартира
                <InputField
                  width='104'
                  ref={register}
                  name='flat'
                  id='flat'
                  type='tel'
                />
              </label>
            </AddressDetails>
          </FormSection>
          <FormSection>
            <Legend>Данные для оплаты</Legend>
            <label htmlFor='cardNumber'>
              <InputFieldwPlaceholder
                ref={register}
                name='cardNumber'
                id='cardNumber'
                type='tel'
                inputMode='numeric'
                autoComplete='cc-number'
                placeholder='Номер карты'
                onChange={e => {
                  const { value } = e.target;
                  setValue('cardNumber', normalizeCardNumber(value));
                }}
              />
              <div>{errors.cardNumber?.message}</div>
              <span>{numberValidation.card && numberValidation.card.type}</span>
            </label>
            <CardDetails>
              <label htmlFor='year'>
                <InputFieldwPlaceholder
                  width='112'
                  ref={register}
                  name='year'
                  id='year'
                  type='tel'
                  placeholder='MM/YYYY'
                />
              </label>
              <label htmlFor='CVV'>
                <InputFieldwPlaceholder
                  width='60'
                  ref={register}
                  name='CVV'
                  id='CVV'
                  type='tel'
                  placeholder='CVV'
                />
              </label>
            </CardDetails>
            <label htmlFor='cardName'>
              <InputFieldwPlaceholder
                ref={register}
                name='cardName'
                id='cardName'
                type='text'
                placeholder='Имя как на карте'
              />
              <div>{errors.cardName?.message}</div>
            </label>
          </FormSection>
          <OrderFooter>
            <OrderFooterSection>
              <span>Стоимость заказа</span> <span>{orderPrice} руб.</span>
            </OrderFooterSection>
            <OrderFooterSection>
              Доставка <span>{PIZZA_DELIVERY.price} руб.</span>
            </OrderFooterSection>
            <DividingLine />
            <OrderFooterSection toPay>
              К оплате <span>{orderPrice + PIZZA_DELIVERY.price} руб.</span>
            </OrderFooterSection>
            <CheckoutFormButton type='submit'>
              Оплатить {orderPrice + PIZZA_DELIVERY.price} руб.
            </CheckoutFormButton>
          </OrderFooter>
        </form>
      </section>
    </>
  );
};
