import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';

const initialState = {
  login: { password: '', email: '' },
  isRegistered: null,
  isLoading: false,
  error: null,
};

export const checkUserAsync = createAsyncThunk(
  'login/loginFetchAsync',
  async (credentials, thunkAPI) => {
    const response = await api.users.login(credentials);
    if (response.status === 200) {
      const results = await response.json();

      thunkAPI.dispatch(setUserStatus(results));
    } else {
      const error = {
        status: response.status,
      };
      thunkAPI.dispatch(setFetchingError(error));
    }
    thunkAPI.dispatch(stopFetching());
  }
);

export const login = createSlice({
  name: 'login',
  initialState,
  reducers: {
    startFetching: state => {
      state.isLoading = true;
    },
    stopFetching: state => {
      state.isLoading = true;
    },
    setFetchingError: (state, action) => {
      state.isLoading = false;
      state.error = action.payload;
    },
    fillUserData: (state, action) => {
      state.login = action.payload;
      state.isLoading = false;
      state.error = null;
    },
    setUserStatus: (state, action) => {
      state.isRegistered = action.payload;
      state.isLoading = false;
      state.error = null;
    },
  },
  extraReducers: {
    [checkUserAsync.fulfilled]: (state, action) => {
      state.login = action.payload;
      state.isLoading = false;
      state.error = null;
    },
    [checkUserAsync.rejected]: state => {
      state.isLoading = false;
      state.error = 'fetching error';
    },
    [checkUserAsync.pending]: state => {}
  },
});
