import { format } from 'date-fns';
import { ru } from 'date-fns/locale';

export const dateShow = () => {
  const now = new Date()
  const formatNow = format(now, 'd MMMM yyy H:mm', {locale: ru});
return formatNow;
}

console.log(dateShow());
